CREATE TABLE lyceums (
    id BIGINT AUTO_INCREMENT PRIMARY KEY,
    name VARCHAR(255),
    chitalishta_url VARCHAR(255),
    status VARCHAR(50),
    bulstat VARCHAR(255) UNIQUE,
    chairman VARCHAR(255),
    secretary VARCHAR(255),
    phone VARCHAR(100),
    email VARCHAR(255),
    region VARCHAR(255),
    municipality VARCHAR(255),
    town VARCHAR(255),
    address VARCHAR(255),
    url_to_libraries_site VARCHAR(255),
    registration_number INT,
    verification_status VARCHAR(50) NOT NULL DEFAULT 'NOT_VERIFIED'
);

ALTER TABLE _users
    ADD COLUMN administrated_lyceum_id BIGINT NULL,
    ADD INDEX idx_users_administrated_lyceum (administrated_lyceum_id),
    ADD CONSTRAINT fk_users_administrated_lyceum
        FOREIGN KEY (administrated_lyceum_id)
        REFERENCES lyceums(id);

